function isHomePage() {
  const e = window.location.pathname.toLowerCase();
  return (
    "/" === e ||
    e.endsWith("index.html") ||
    e.includes("/home") ||
    e.includes("/inicio")
  );
}
document.querySelectorAll("[data-include]").forEach(async (e) => {
  const t = e.getAttribute("data-include");
  try {
    const o = await fetch(t);
    if (!o.ok) throw new Error(`Erro ao carregar ${t}`);
    const n = await o.text();
    e.innerHTML = n;
    const r = e.querySelectorAll(".carousel");
    r.forEach((e) => {
      new bootstrap.Carousel(e, { interval: 3e3, ride: "carousel" });
    });
    const c = e.querySelectorAll(".modal[id^='modal-video-']");
    c.forEach((e) => {
      const t = e.querySelector("iframe"),
        o = t?.getAttribute("data-src");
      e.addEventListener("show.bs.modal", () => {
        t && o && t.setAttribute("src", o);
      }),
        e.addEventListener("hidden.bs.modal", () => {
          t && t.setAttribute("src", "");
        });
    }),
      isHomePage() &&
        (() => {
          const t = e.querySelector("#site-logo");
          if (t && !t.closest("h1")) {
            const e = document.createElement("h1");
            (e.className = "mb-0"),
              t.parentNode.insertBefore(e, t),
              e.appendChild(t);
          }
        })();
  } catch (o) {
    console.error(o),
      (e.innerHTML = `<p class="text-danger">Erro ao carregar "${t}"</p>`);
  }
}),
  window.addEventListener("load", () => {
    const e = window.location.hash;
    if (e) {
      const t = () => {
        const t = document.querySelector(e);
        t ? t.scrollIntoView({ behavior: "smooth" }) : setTimeout(t, 100);
      };
      t();
    }
  });
function aceitarCookies() {
  localStorage.setItem("cookies-aceitos", "true");
  const e = document.getElementById("cookieConsent");
  e && (e.style.display = "none");
}
window.addEventListener("load", () => {
  if (!localStorage.getItem("cookies-aceitos")) {
    const e = document.getElementById("cookieConsent");
    e && (e.style.display = "block");
  }
}),
  "serviceWorker" in navigator &&
    navigator.serviceWorker
      .register("service-worker.js")
      .then((e) => console.log("Service Worker registrado!", e))
      .catch((e) => console.error("Erro ao registrar SW:", e));
